# Company -> NeobyteSolutions
# Link ----> https://www.neobytesolutions.com/careers/
#
import scrapy
from JobsCrawlerProject.items import JobItem
#
import uuid


class NeobytesolutionsSpiderSpider(scrapy.Spider):
    name = "neobytesolutions_spider"
    allowed_domains = ["www.neobytesolutions.com"]
    start_urls = ["https://www.neobytesolutions.com/careers/"]

    def start_requests(self):
        yield scrapy.Request(self.start_urls[0])

    def parse(self, response):

        # parse jobs
        for job in response.css('div.panel.panel-default'):
            item = JobItem()
            item['id'] = str(uuid.uuid4())
            item['job_link'] = job.css('div.panel-body').css('a::attr(href)').get().strip()
            item['job_title'] = job.css('h4.panel-title::text').get()
            item['company'] = 'NeobyteSolutions'
            item['country'] = 'Romania'
            item['city'] = 'Oradea'
            item['logo_company'] = 'data:image/png;base64,'
            #
            yield item
